import React, { useEffect, useState } from 'react'
import PrimaryButton from '../components/primary-button'
import posts from '../data/posts'
import BasicPageLayout, { BlogPageLayout } from '../layouts/basic-page'
import DB from '../data/db'
import ReactMarkdown from 'react-markdown'
import ReactDom from 'react-dom'

const Post = (props) => {

  const [post, setPost] = useState(null);
  const { name } = props.match.params


  useEffect(() => {
    DB.getPostBySlug(name).then((data) => {
      console.log("post", data)
      setPost(data)
    })
  }, [])

  return (
    <BlogPageLayout cardsTitle={""} cardsSubtitle={""}>
      <div
        style={{ zIndex: 100, width: "100%", maxWidth: 1050, minHeight: 600 }}
      >
        {post && (
          <div>
            <div
              style={{ fontSize: 34, fontWeight: 800 }}
            >
              {post.title}
            </div>
            <div>
              <ReactMarkdown children={post.body} />
            </div>
          </div>
        )}



      </div>

    </BlogPageLayout>
  )
}

export default Post
