                <br></br>
                <br className="home-text22"></br>

              </span>
              {details.buttonTitle && (
                <a
                  href={details.buttonUrl}
                  target="_blank"
                  rel="noreferrer noopener"
                  className="home-link04"
                >
                  <PrimaryButton
                    button={details.buttonTitle}
                    className="home-component"
                  ></PrimaryButton>
                </a>
              )}
            </div>
            <div style={{
              flex: 1,
              minWidth: 300
            }}>
              <div style={{
                border: "0px solid black",
                borderRadius: 10,
                zIndex: 100,
                backgroundColor: "#fff",
                boxShadow: "0px 0px 5px 0px rgba(0,0,0,0.25)",
                overflow: "hidden",
                flex: 1,
                width: "100%",
                margin: 0
              }}>
                <iframe style={{ overflow: "hidden", marginBottom: -10 }} width='100%' height={details?.formHeight} src={details?.form}></iframe>
              </div>
            </div>
          </div>
        </div>
        <img
          alt="image"
          src="/turquoise-circle.svg"
          className="home-turquoise-cirble"
        />
        <img
          alt="image"
          src="/purple-circle.svg"
          className="home-purple-circle"
        />
      </div>
    </BlogPageLayout>
  )
}

export default Post
