import React, { useEffect, useState } from "react";
import PrimaryButton from "../components/primary-button";
import posts from "../data/posts";
import BasicPageLayout, { BlogPageLayout } from "../layouts/basic-page";
import DB from "../data/db";
import ReactMarkdown from "react-markdown";
import ReactDom from "react-dom";
import moment from "moment";

const Post = (props) => {
  const [post, setPost] = useState(null);
  const { name } = props.match.params;
  const [body, setBody] = useState("");

  useEffect(() => {
    DB.getPostBySlug(name).then((data) => {
      console.log("post", data);
      setPost(data);
      let markdown = data.body;
      let html = markdownToHtml(markdown);
      setBody(html);
    });
  }, []);

  // convert markdown to html
  function markdownToHtml(markdown) {
    // bold to html bold
    markdown = markdown.replace(/\*\*(.*?)\*\*/g, "<b>$1</b>");
    // italic to html italic
    markdown = markdown.replace(/\*(.*?)\*/g, "<i>$1</i>");
    // code to html code
    markdown = markdown.replace(/`(.*?)`/g, "<code>$1</code>");
    // link to html link
    markdown = markdown.replace(/\[(.*?)\]\((.*?)\)/g, '<a href="$2">$1</a>');
    // image to html image
    markdown = markdown.replace(
      /!\[(.*?)\]\((.*?)\)/g,
      '<img src="$2" alt="$1" />'
    );
    // new line to html br
    markdown = markdown.replace(/\n/g, "<br />");

    return markdown;
  }

  return (
    <BlogPageLayout>
      <div
        style={{ zIndex: 100, width: "100%", maxWidth: 1050, minHeight: 600 }}
      >
        {post && (
          <div style={{ padding: 40 }}>
            <div
              style={{
                backgroundImage: "url(" + post.image.url + ")",
                backgroundSize: "cover",
                backgroundPosition: "center",
                width: "100%",
                height: 500,
                borderRadius: 10,
              }}
            ></div>
            <div style={{ fontSize: 52, fontWeight: 700, marginTop: 20 }}>
              {post.title}
            </div>
            <div style={{ fontSize: 14, color: "#8C30F5" }}>
              {moment(post.publishedAt).format("LL")}
            </div>
            <div
              style={{
                fontSize: 14,
                color: "grey",
                marginBottom: 40,
                borderBottom: "1px solid #ccc",
              }}
            >
              {post.subtitle}
            </div>

            <div dangerouslySetInnerHTML={{ __html: body }}></div>
          </div>
        )}
      </div>
    </BlogPageLayout>
  );
};

export default Post;
