import React from 'react'
import PrimaryButton from '../components/primary-button'
import posts from '../data/posts'
import BasicPageLayout, { BlogPageLayout } from '../layouts/basic-page'



const Posts = (props) => {


  // url params name
  const { name } = props.match.params


  return (
    <BlogPageLayout cardsTitle={"posts"} cardsSubtitle={details?.cardsSubtitle}>

      <div
        style={{ zIndex: 100, width: "100%", maxWidth: 1050, }}
      >
        <div
          style={{ display: "flex", flexWrap: "wrap", height: "100%", paddingTop: 50, paddingBottom: 150 }}
        >
          <div
            style={{ flex: 1, minWidth: 300, padding: 20 }}
          >

            <div style={{
              fontWeight: 900,
              fontSize: 44,
              fontFamily: "Inter",
              fontWeight: 800,
              lineHeight: 1,
              paddingBottom: 14
            }}>
              {details?.title.map((line) => {
                return (
                  <>
                    {line}  <br />
                  </>
                )
              })}
            </div>
            <span >
              {details?.subtitle.map((line) => {
                return (
                  <>
                    {line}   <br />
                  </>
                )
              })}
              <div
                style={{
                  fontSize: 24,
                }}
              >
                {details.dates && details?.dates.map((line) => {
                  return (
                    <>
                      {line}   <br />
                    </>
                  )
                })}

              </div>
              {details.dates && (<div style={{ fontSize: "small" }}>(recording available after event)</div>)}

              <br></br>
              <br className="home-text22"></br>

            </span>
            {details.buttonTitle && (
              <a
                href={details.buttonUrl}
                target="_blank"
                rel="noreferrer noopener"
                className="home-link04"
              >
                <PrimaryButton
                  button={details.buttonTitle}
                  className="home-component"
                ></PrimaryButton>
              </a>
            )}
          </div>
          <div style={{
            flex: 1,
            minWidth: 300
          }}>
            <div style={{
              border: "0px solid black",
              borderRadius: 10,
              zIndex: 100,
              backgroundColor: "#fff",
              boxShadow: "0px 0px 5px 0px rgba(0,0,0,0.25)",
              overflow: "hidden",
              flex: 1,
              width: "100%",
              margin: 0
            }}>
              <iframe style={{ overflow: "hidden", marginBottom: -10 }} width='100%' height={details?.formHeight} src={details?.form}></iframe>
            </div>
          </div>
        </div>
      </div>
    </BlogPageLayout>
  )
}

export default Posts
