import React from 'react'

import PropTypes from 'prop-types'

import './feature-card.css'
import FeatureCard from './feature-card';
import { Helmet } from 'react-helmet';

const PageHeader = (props) => {

    function isMobile() {
        return window.innerWidth < 768;
    }

    function MenuItem({ href, label }) {
        let selected = window.location.pathname.includes(href);

        return (
            <div style={{ padding: 4, margin: 4, fontWeight: selected ? 800 : 3000 }}>
                <a href={href} className="home-link">{label}</a>
            </div>
        );
    }


    function ButtonItem({ href, label }) {
        let selected = window.location.pathname.includes(href);

        return (
            <div style={{ padding: "1px 8px", height: 30, minWidth: 60, textAlign: "center", margin: 4, fontWeight: selected ? 800 : 3000, borderRadius: 8, border: "1px solid black" }}>
                <a href={href} className="home-link">{label}</a>
            </div>
        );
    }

    return (
        <>
            <Helmet>
                <title>Valid</title>
                <meta
                    name="description"
                    content="Valid accelerates insurance for care providers by offering real-time verifications"
                />
                <meta property="og:title" content="Valid" />
                <meta
                    property="og:description"
                    content="Valid accelerates insurance for care providers by offering real-time verifications…… So that they can get paid quicker and eliminate the threat of denied claims.  "
                />

            </Helmet>
            <div data-role="Header" className="home-header-container">
                <header className="home-header">
                    <div style={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                        padding: "10px 20px",
                        borderBottom: "0px solid #e0e0e0",
                        flex: 1,
                        flexWrap: "wrap",
                        flexDirection: isMobile() ? "column" : "row"
                    }}>
                        <div style={{ flex: 1, marginBottom: 40 }}>
                            <a href="/">
                                <div className="home-logo" >
                                    <img alt="image" src="/5_crop1-200h.png" className="home-image" />
                                </div>
                            </a>
                        </div>
                        <div style={{ display: "flex", flexWrap: "wrap", flexDirection: isMobile() ? "column" : "row", width: "100%" }}>
                            <MenuItem href="/post" label="Blog" />
                            <MenuItem href="/about" label="About" />
                            <MenuItem href="/contact" label="Contact" />
                            <ButtonItem href="/login" label="Login" />
                            <ButtonItem href="/signup" label="Sign Up" />
                        </div>
                    </div>
                </header>
            </div>
        </>
    );
}


export default PageHeader;
