import React from 'react';
import { ApolloClient, InMemoryCache, gql, useQuery } from '@apollo/client';


const client = new ApolloClient({
    uri: 'https://app-us-east-1-shared-usea1-02.hygraph.com/v2/d7af2244f18049e3bbc371780eeafa77/master',
    cache: new InMemoryCache(),
});

const GET_PRODUCTS_QUERY = gql`
  query GetProducts {
    items {
      id
      name
      description
    }
  }
`;

//get posts query
const GET_POSTS_QUERY = gql`
    query GetPosts {
        posts {
        title
        subtitle
        dates
        image
        slug
        }
    }
    `;

const DB = {
    getPosts: async () => {
        const { data } = await client.query({
            query: GET_POSTS_QUERY
        });
        return data.items;
    }
}

export default DB;
