import React, { useEffect, useState } from "react";
import PrimaryButton from "../components/primary-button";
import BasicPageLayout, { BlogPageLayout } from "../layouts/basic-page";
import DB from "../data/db";
import ReactMarkdown from "react-markdown";
import ReactDom from "react-dom";
import moment from "moment";
import "./home.css";

const About = (props) => {
  const [page, setPage] = useState(null);
  const { name } = props.match.params;

  return (
    <BlogPageLayout>
      <div
        style={{ zIndex: 100, width: "100%", maxWidth: 1050, minHeight: 600 }}
      >
        <div>
          <div>
            At Valid, we're on a mission to transform the landscape of
            healthcare administration by streamlining insurance verification and
            claims management processes. With a focus on empowering care
            providers, we offer a unified Verification and Claims API Platform
            that revolutionizes the way healthcare organizations navigate the
            complexities of insurance systems.
          </div>
          <div style="display:flex; padding-top:80px">
            <div style="flex:1">
              <div
                style="
background-image:url(https://us-east-1-shared-usea1-02.graphassets.com/clulby54r00av07lh1umf40ff/cluop5q7gmqf207k4j8248t40);
background-size:cover;
height:260px;
width:260px;
border-radius:10px;
margin-left:auto;
margin-right:auto;
"
              ></div>
            </div>
            <div style="flex:1">
              <div style="font-weight:700; font-size:34px">Our Vision</div>
              We envision a future where healthcare providers can focus their
              time and resources on delivering exceptional patient care rather
              than being burdened by administrative tasks. By leveraging
              cutting-edge technology and innovative solutions, we aim to create
              a seamless experience for both providers and patients within the
              healthcare ecosystem.
            </div>
          </div>
          <div style="font-weight:700; font-size:54px; text-align:center; padding-top:120px; padding-bottom:20px">
            Team
          </div>
          <div style="display:flex">
            <div style="flex:1; padding:40px; text-align:center">
              <div
                style="
background-image:url(https://us-east-1-shared-usea1-02.graphassets.com/clulby54r00av07lh1umf40ff/clunevllmgfr107lfueb04ivr);
background-size:cover;
height:200px;
width:200px;
border-radius:100px;
margin-left:auto;
margin-right:auto;
"
              ></div>
              <div style="font-weight:700; font-size:32px">Jon Rhoades</div>
              <div style="font-weight:400; font-size:22px; padding-bottom:40px">
                Technology Lead
              </div>
              With over 18 years of experience in the technology industry, Jon
              Rhoades brings a wealth of expertise to our team. His background
              includes spearheading major technology rollouts and fostering
              startup culture, making him instrumental in driving our
              technological innovation forward.
              <div style="font-weight:600;padding-top:40px">
                <a href="https://www.linkedin.com/in/jon-rhoades/">
                  Connect with Jon on LinkedIn
                </a>
              </div>
            </div>
            <div style="flex:1; padding:40px; text-align:center">
              <div
                style="
background-image:url(https://us-east-1-shared-usea1-02.graphassets.com/clulby54r00av07lh1umf40ff/cluneuogugfjd07k4ewneayql);
background-size:cover;
height:200px;
width:200px;
border-radius:100px;
margin-left:auto;
margin-right:auto;
"
              ></div>
              <div style="font-weight:700; font-size:32px">Chase Francl</div>
              <div style="font-weight:400; font-size:22px; padding-bottom:40px">
                Healthcare Specialist
              </div>
              As the founder of ValiData Outcome Services and President/CEO of
              the Mid-Plains Center for Behavioral Healthcare Services, Inc.,
              Chase Francl is deeply rooted in the healthcare industry. His
              firsthand knowledge of provider challenges and commitment to
              offering impactful solutions make him an invaluable asset to our
              team.
              <div style="font-weight:600;padding-top:40px">
                <a href="https://www.linkedin.com/in/chase-francl-3342b2b1/">
                  Connect with Chase on LinkedIn
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </BlogPageLayout>
  );
};

export default About;
