import React from 'react'
import PrimaryButton from '../components/primary-button'
import posts from '../data/posts'
import BasicPageLayout, { BlogPageLayout } from '../layouts/basic-page'



const Post = (props) => {


  // url params name
  const { name } = props.match.params

  let details = posts[name];

  return (
    <BlogPageLayout cardsTitle={details?.cardsTitle} cardsSubtitle={details.cardsSubtitle}>
      <div className="home-main">
        <div className="home-blur-background"></div>
        <div
          style={{ zIndex: 100, width: "100%", maxWidth: 1050, }}
        >
          <div
            style={{ display: "flex", flexWrap: "wrap", height: "100%", paddingTop: 50, paddingBottom: 150 }}
          >
            <div
              style={{ flex: 1, minWidth: 300, padding: 20 }}
            >

              <div style={{
                fontWeight: 900,
                fontSize: 44,
                fontFamily: "Inter",
                fontWeight: 800,
                lineHeight: 1,
                paddingBottom: 14
              }}>
                {details?.title.map((line) => {
                  return (
                    <>
                      {line}  <br />
                    </>
                  )
                })}
              </div>
              <span >
                {details?.subtitle.map((line) => {
                  return (
                    <>
                      {line}   <br />
                    </>
                  )
                })}
                <div
                  style={{
                    fontSize: 24,
                  }}
                >
                  {details.dates && details?.dates.map((line) => {
                    return (
                      <>
                        {line}   <br />
                      </>
                    )
                  })}

                </div>
                {details.dates && (<div style={{ fontSize: "small" }}>(recording available after event)</div>)}

                <br></br>
                <br className="home-text22"></br>

              </span>

            </div>

          </div>
        </div>
        <img
          alt="image"
          src="/turquoise-circle.svg"
          className="home-turquoise-cirble"
        />
        <img
          alt="image"
          src="/purple-circle.svg"
          className="home-purple-circle"
        />
      </div>
    </BlogPageLayout>
  )
}

export default Post
