import React from 'react'
import PrimaryButton from '../components/primary-button'
import posts from '../data/posts'
import BasicPageLayout, { BlogPageLayout } from '../layouts/basic-page'



const PostsPage = (props) => {


  // url params name
  const { name } = props.match.params

  //post 
  function PostCard(post) {
    return (
      <div>
        Post Card
      </div>
    )
  }

  function PostList() {
    return (
      <div>
        {posts && posts.map((post) => {
          return (
            <PostCard post={post} />
          )
        })}
      </div>
    )
  }


  return (
    <BlogPageLayout cardsTitle={"posts"} cardsSubtitle={"post details"}>

      <div
        style={{ zIndex: 100, width: "100%", maxWidth: 1050, minHeight: 800 }}
      >
        <div
          style={{ flex: 1, minWidth: 300, paddingTop: 60, paddingLeft: 40 }}
        >
          <div style={{
            fontWeight: 900,
            fontSize: 44,
            fontFamily: "Inter",
            fontWeight: 800,
            lineHeight: 1,
            paddingBottom: 14
          }}>Posts</div>
          <div>
            <PostList />
          </div>
        </div>
      </div>
    </BlogPageLayout>
  )
}

export default PostsPage
